CREATE OR REPLACE VIEW dashboard_projeto_v (nr_sequencia, ds_titulo, cd_coordenador, nm_coordenador, dt_atualizacao, qt_hpi, qt_spi, nr_seq_gerencia, ie_origem, ie_status) AS select	nr_sequencia,
	ds_titulo,
	cd_coordenador,
	substr(obter_nome_pf(cd_coordenador),1,100) nm_coordenador,
	(select max(c.dt_atualizacao)
	FROM	proj_cronograma b,
			proj_cron_etapa c
	where	c.nr_seq_cronograma = b.nr_sequencia
	and		b.nr_seq_proj = a.nr_sequencia) dt_atualizacao,
	pmo_obter_dados_bsc(a.nr_sequencia,'HPI','PROJ') qt_hpi,
    pmo_obter_dados_bsc(a.nr_sequencia,'SPI','PROJ') qt_spi,
	nr_seq_gerencia,
	ie_origem,
	ie_status
 from proj_projeto a;

