SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW data_ordem_compra_pend_v (cd_material_estoque, dt_entrega, cd_estabelecimento) AS select 	x.cd_material_estoque,
	c.dt_prevista_entrega dt_entrega,
	b.cd_estabelecimento
FROM 	parametro_compras p,
	material x,
	ordem_compra b,
	ordem_compra_item a,
	ordem_compra_item_entrega c
where 	p.cd_estabelecimento	= b.cd_estabelecimento
and	b.dt_baixa 		is null
and 	a.nr_ordem_compra	= b.nr_ordem_compra
and 	a.nr_ordem_compra	= c.nr_ordem_compra
and	a.nr_item_oci		= c.nr_item_oci
and	a.cd_material		= x.cd_material
and (coalesce(c.qt_prevista_entrega,0) - coalesce(c.qt_real_entrega,0)) > 0
and CASE WHEN p.ie_data_base_compra_pend='DT_ATUALIZACAO' THEN  a.dt_atualizacao WHEN p.ie_data_base_compra_pend='DT_ORDEM_COMPRA' THEN  b.dt_ordem_compra WHEN p.ie_data_base_compra_pend='DT_ENTREGA' THEN  b.dt_entrega END  > LOCALTIMESTAMP - coalesce(p.qt_dia_compra_pend, 180)
and	c.dt_cancelamento is null
and	b.nr_seq_motivo_cancel is null
and	coalesce(ie_ordem_pendente,'S') = 'S';

