SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW ecd_saldo_periodico_empresa_v (tp_registro, ds_identificador, cd_empresa, cd_estabelecimento, dt_inicial, dt_final, cd_conta_contabil, cd_classificacao, cd_centro_custo, vl_saldo_inicial, ie_debito_credito_inicial, vl_debito, vl_credito, vl_saldo_final, ie_debito_credito_final, barra_final) AS select	distinct
	1 tp_registro,
	'|I150' ds_identificador,
	b.cd_empresa,
	null cd_estabelecimento,
	b.dt_referencia dt_inicial,
	fim_mes(b.dt_referencia) dt_final,
	'' 	cd_conta_contabil,
	''	cd_classificacao,
	null	cd_centro_custo,
	0	vl_saldo_inicial,
	''	ie_debito_Credito_inicial,
	0	vl_debito,
	0	vl_credito,
	0	vl_saldo_final,
	'' 	ie_debito_credito_final,
	'|' barra_final
FROM	ctb_mes_ref b,
	ctb_saldo a
where	a.nr_seq_mes_ref	= b.nr_sequencia

union all

select	2 tp_registro,
	'|I155' ds_identificador,
	b.cd_empresa,
	null cd_estabelecimento,
	b.dt_referencia		dt_inicial,
	fim_mes(b.dt_referencia)	dt_final,
	a.cd_conta_contabil,
	c.cd_classificacao,
	a.cd_centro_custo,
	(sum(a.vl_saldo) - sum(a.vl_movimento)) vl_saldo_inicial,
	substr(ctb_obter_situacao_saldo(a.cd_conta_contabil,  (sum(a.vl_saldo) - sum(a.vl_movimento))),1,1) ie_debito_credito_inicial,
	sum(a.vl_debito) vl_debito,
	sum(a.vl_credito) vl_credito,
	sum(a.vl_saldo) vl_saldo_final,
	substr(ctb_obter_situacao_saldo(a.cd_conta_contabil,  sum(a.vl_saldo)),1,1) || '|' ie_debito_credito_final,
	'|' barra_final
from	ctb_mes_ref b,
	conta_contabil c,
	ctb_saldo a
where	a.nr_seq_mes_ref = b.nr_sequencia
and	c.cd_conta_contabil = a.cd_conta_contabil
and	c.ie_tipo = 'A'
group by b.dt_referencia,
	  b.cd_empresa,
	a.cd_conta_contabil,
	c.cd_classificacao,
	a.cd_centro_custo
order by dt_inicial,tp_registro,cd_conta_contabil,cd_centro_custo;

