CREATE OR REPLACE VIEW eis_ansi_depre_v (cd_setor_atendimento, ds_estabelecimento, cd_convenio, ds_convenio, ds_setor_atendimento, ie_sexo, ds_sexo, nm_medico, cd_pessoa_fisica, cd_medico_resp, ie_faixa_etaria, ds_unidade, dt_avaliacao, qt_ansiedade, qt_depressao, cd_empresa, cd_estabelecimento, ds_ansiedade, ds_depressao, nr_atendimento, cd_paciente, ie_turno) AS select	distinct
	eis_obter_setor_atend_data(a.nr_atendimento, e.dt_avaliacao) cd_setor_atendimento, 
	substr(obter_nome_estabelecimento(a.cd_estabelecimento),1,80) ds_estabelecimento, 
