	obter_sexo_pf(a.cd_pessoa_fisica,'C') ie_sexo, 
	obter_sexo_pf(a.cd_pessoa_fisica,'D') ds_sexo, 
	obter_nome_pessoa_fisica(e.cd_profissional, null) nm_medico, 
	a.cd_pessoa_fisica, 
	a.cd_medico_resp, 
