CREATE OR REPLACE VIEW eis_cih_infeccao_sec_v (dt_referencia, cd_clinica, cd_topografia, cd_caso_infeccao, cd_setor_atendimento, qt_1, ds_clinica, ds_topografia, ds_caso_infec, ds_setor_atendimento, ds_classif_topografia) AS SELECT  TRUNC(a.dt_alta_interno) DT_REFERENCIA,
        b.cd_clinica,
        b.cd_topografia,
        b.cd_caso_infeccao,
        b.cd_setor_atendimento,
        COUNT(*) qt_1,
        coalesce(substr(obter_nome_clinica(b.cd_clinica),1,60),'Não informado') ds_clinica,
        coalesce(substr(obter_desc_topografia(b.cd_topografia),1,60),'Não informado') ds_topografia,
        coalesce(SUBSTR(obter_desc_caso_infeccao(b.cd_caso_infeccao),1,60),'Não informado') ds_caso_infec,
        coalesce(substr(obter_nome_setor(b.cd_setor_atendimento),1,100),'Não informado') ds_setor_atendimento,
        coalesce((select max(c.ds_classificacao) ds
        FROM    cih_classif_topografia c
        where   c.nr_sequencia = b.nr_seq_classif_top),'Não informado') ds_classif_topografia
FROM    cih_ficha_ocorrencia_v a,
        cih_local_infeccao b
WHERE   a.nr_ficha_ocorrencia  =  b.nr_ficha_ocorrencia
GROUP   BY b.cd_caso_infeccao, a.dt_alta_interno,
        b.cd_clinica,
        b.cd_topografia,
        b.cd_caso_infeccao,
        b.cd_setor_atendimento,
        b.nr_seq_classif_top;

