CREATE OR REPLACE VIEW eis_devolucao_mat_med_v (dt_devolucao, nm_usuario_devol, cd_pessoa_fisica_devol, cd_setor_atendimento, ds_setor_atendimento, cd_material, ds_material, cd_motivo_devolucao, ds_motivo_devol, ds_local_estoque, cd_local_estoque, cd_centro_custo, ds_centro_custo, cd_grupo_material, cd_subgrupo_material, qt_devolvida, vl_custo_medio, nm_pessoa_dev, nr_atendimento, cd_tipo_devolucao, ds_tipo_devolucao, cd_estabelecimento, ds_subgrupo_material) AS select	a.dt_atendimento dt_devolucao,
