	obter_quantidade_convertida(a.cd_material,a.qt_devolvida,a.cd_unidade_medida,'UMC') qt_devolvida,
	dividir(obter_custo_medio_material(Obter_Dados_Atendimento(a.nr_atendimento,'EST'),trunc(a.dt_atendimento,'month'),a.cd_material), coalesce(b.qt_conv_estoque_consumo,1)) vl_custo_medio,
