CREATE OR REPLACE VIEW eis_escala_avei_v (cd_setor_atendimento, ds_estabelecimento, cd_convenio, ds_convenio, ds_setor_atendimento, ie_sexo, ds_sexo, nm_medico, cd_profissional, ie_faixa_etaria, ds_unidade, dt_avaliacao, qt_pontuacao, cd_empresa, cd_estabelecimento, nr_atendimento, ds_grau) AS SELECT	coalesce(a.cd_setor_atendimento,obter_setor_atend_data(a.nr_atendimento, e.dt_avaliacao)) cd_setor_atendimento,
	SUBSTR(obter_nome_estabelecimento(a.cd_estabelecimento),1,80) ds_estabelecimento,
	obter_convenio_atendimento(a.nr_atendimento) cd_convenio,
	SUBSTR(obter_nome_convenio(obter_convenio_atendimento(a.nr_atendimento)),1,150) ds_convenio,
	obter_nome_setor(coalesce(a.cd_setor_atendimento,obter_setor_atend_data(a.nr_atendimento, e.dt_avaliacao))) ds_setor_atendimento,
	obter_sexo_pf(a.cd_pessoa_fisica,'C') ie_sexo,
	obter_sexo_pf(a.cd_pessoa_fisica,'D') ds_sexo,
	obter_nome_pessoa_fisica(e.cd_profissional, NULL) nm_medico,
	e.cd_profissional,
	SUBSTR(obter_idade(obter_data_nascto_pf(a.cd_pessoa_fisica),LOCALTIMESTAMP,'E'),1,10) ie_faixa_etaria,
	obter_unidade_atendimento(a.nr_atendimento,'A','U') ds_unidade,
	e.dt_avaliacao,
	e.qt_pontuacao,
	f.cd_empresa,
	a.cd_estabelecimento,
	a.nr_atendimento,
	SUBSTR(obter_desc_escala_avei(qt_pontuacao),1,100) ds_grau
FROM 	escala_avei e,
	estabelecimento f,
	atendimento_paciente_v a
WHERE 	a.nr_atendimento= e.nr_atendimento
AND	a.cd_estabelecimento = f.cd_estabelecimento
