CREATE OR REPLACE VIEW eis_internacao_uti_v (nr_atendimento, cd_pessoa_fisica, ie_clinica, cd_setor_atendimento, ds_setor_atendimento, nm_pessoa_fisica, dt_entrada, dt_entrada_unidade, dt_saida_unidade, qt_dias_uti, cd_estabelecimento) AS select	a.nr_atendimento,
