CREATE OR REPLACE VIEW eis_market_share_v (sg_estado, ds_estado, ds_regiao, qt_mercado, cd_cnpj, nm_empresa, qt_cliente) AS select	sg_estado,
	substr(obter_valor_dominio(50,sg_estado),1,200) ds_estado, 
	substr(obter_regiao_uf(sg_estado),1,100) ds_regiao, 
	coalesce(qt_mercado,0) qt_mercado, 
	cd_cnpj, 
	substr(obter_nome_pf_pj(null, cd_cnpj),1,50) nm_empresa, 
	coalesce(qt_cliente,0) qt_cliente 
FROM	eis_market_share;

