SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW eis_mensal_exame_result_v (ds_exame, qt_exame, dt_exame, ds_estabelecimento, cd_estabelecimento, nm_paciente, nr_atendimento) AS select 	b.ds_exame ds_exame,
	sum(a.nr_transf) qt_exame,
	trunc(a.dt_exame) dt_exame,
	substr(obter_nome_estabelecimento(b.cd_estabelecimento),1,80) ds_estabelecimento,
	b.cd_estabelecimento,
	a.nm_paciente nm_paciente,
	a.nr_atendimento nr_atendimento
FROM	san_exame b,
		san_exame_v a
where 	a.nr_seq_exame = b.nr_sequencia
and 	a.ie_exame = 2
and 	a.ds_resultado <> 'NR'
group by	b.ds_exame,
			a.nr_transf,
			a.dt_exame,
			substr(obter_nome_estabelecimento(b.cd_estabelecimento),1,80),
			b.cd_estabelecimento,
			a.nm_paciente,
			a.nr_atendimento;

