SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW eis_meta_compra_v (dt_referencia, cd_estabelecimento, cd_grupo_material, ds_grupo_material, vl_meta, vl_compra, pr_percentual) AS select	a.dt_referencia,
	a.cd_estabelecimento,
	a.cd_grupo_material,
	SUBSTR(obter_desc_grupo_mat(a.cd_grupo_material),1,80) ds_grupo_material,
	a.vl_compra vl_meta,
	SUM(b.vl_compra) vl_compra,
	(dividir((SUM(b.vl_compra) - a.vl_compra), a.vl_compra) * 100) pr_percentual
FROM	eis_meta_compra a,
	eis_compras b
where	a.cd_estabelecimento = b.cd_estabelecimento
and	a.cd_grupo_material = b.cd_grupo_material
and	a.dt_referencia = b.dt_referencia
and 	b.ie_periodo = 'M'
group by
	a.dt_referencia,
	a.cd_estabelecimento,
	a.cd_grupo_material,
	a.vl_compra;

