CREATE OR REPLACE VIEW eis_meta_estoque_v (dt_referencia, cd_estabelecimento, cd_grupo_material, ds_grupo_material, vl_meta, vl_estoque, vl_media, pr_percentual) AS select	a.dt_referencia,
	a.cd_estabelecimento,
	a.cd_grupo_material,
	substr(obter_desc_grupo_mat(a.cd_grupo_material),1,80) ds_grupo_material,
	a.vl_estoque vl_meta,
	sum(b.vl_estoque) vl_estoque,
	dividir(sum(b.vl_estoque), obter_periodo_estoque(trunc(a.dt_referencia, 'mm'), fim_mes(a.dt_referencia), a.cd_estabelecimento)) vl_media,
	(dividir((SUM(b.vl_estoque) - a.vl_estoque), a.vl_estoque) * 100) pr_percentual
FROM	material m,
	eis_meta_estoque a,
	eis_saldo_estoque b
where	a.cd_estabelecimento	= b.cd_estabelecimento
and	a.cd_grupo_material	= b.cd_grupo_material
and	a.dt_referencia		= b.dt_referencia
and	b.cd_material		= m.cd_material
and	m.ie_consignado		= a.ie_consignado
group by
	a.dt_referencia,
	a.cd_estabelecimento,
	a.cd_grupo_material,
	a.vl_estoque;

