SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW eis_meta_giro_v (dt_referencia, cd_estabelecimento, cd_grupo_material, ds_grupo_material, vl_meta, vl_giro, pr_percentual) AS select	a.dt_referencia,
	a.cd_estabelecimento,
	a.cd_grupo_material,
	SUBSTR(obter_desc_grupo_mat(a.cd_grupo_material),1,80) ds_grupo_material,
	a.vl_giro vl_meta,
	dividir(sum(vl_consumo), sum(vl_estoque_medio)) vl_giro,
	(dividir((dividir(sum(vl_consumo), sum(vl_estoque_medio)) - a.vl_giro), a.vl_giro) * 100) pr_percentual
FROM	eis_meta_giro a,
	eis_saldo_estoque_v b
where	a.cd_estabelecimento = b.cd_estabelecimento
and	a.cd_grupo_material = b.cd_grupo_material
and	a.dt_referencia = b.dt_referencia
group by
	a.dt_referencia,
	a.cd_estabelecimento,
	a.cd_grupo_material,
	a.vl_giro;

