CREATE OR REPLACE VIEW eis_meta_result_conv_prod_v (cd_estabelecimento, cd_convenio, ds_convenio, dt_referencia, vl_hospital, vl_meta, pr_percentual) AS select	/*+INDEX (a EISRESU_I1) */	a.cd_estabelecimento,
	a.cd_convenio,
	SUBSTR(obter_nome_convenio(a.cd_convenio),1,100) ds_convenio,
	a.dt_receita dt_referencia,
	sum(a.vl_Procedimento + a.vl_Material + a.Vl_terceiro) vl_hospital,
	b.vl_producao vl_meta,
	(dividir((sum(a.vl_Procedimento + a.vl_Material + a.Vl_terceiro) - b.vl_producao), b.vl_producao) * 100) pr_percentual
FROM	EIS_META_RESULT_CONV_PROD b,
	eis_resultado a
where	a.cd_convenio	= b.cd_convenio
and	a.dt_receita = trunc(b.dt_referencia, 'month')
and 	somente_numero(a.ie_protocolo) >= 2
and 	somente_numero(a.ie_protocolo) <= 10
GROUP BY a.cd_estabelecimento,
	a.cd_convenio,
	SUBSTR(obter_nome_convenio(a.cd_convenio),1,100),
	a.dt_receita,
	b.vl_producao;

