CREATE OR REPLACE VIEW eis_ops_diarias_v (dt_mes_competencia, cd_estabelecimento, ds_procedimento, ds_grupo_despesa, ds_tipo_grupo_ans, qt_liberada, vl_custo) AS select	pkg_date_utils.start_of(a.dt_mes_competencia, 'MONTH', 0) dt_mes_competencia,
	b.cd_estabelecimento,
	substr(obter_descricao_procedimento(c.cd_procedimento, c.ie_origem_proced),1,255) ds_procedimento,
	substr(h.ds_grupo,1,50) ds_grupo_despesa,
	substr(obter_valor_dominio(3579, h.ie_tipo_grupo_ans),1,50) ds_tipo_grupo_ans,
	c.qt_procedimento qt_liberada,
	c.vl_liberado vl_custo
FROM	pls_protocolo_conta a,
	pls_conta b,
	pls_conta_proc c,
	ans_grupo_despesa h
where	a.nr_sequencia = b.nr_seq_protocolo
and	b.nr_sequencia = c.nr_seq_conta
and	c.nr_seq_grupo_ans = h.nr_sequencia
and	coalesce(c.qt_procedimento,0) > 0
and	c.ie_tipo_despesa = 3;

