CREATE OR REPLACE VIEW eis_ops_exames_cons_benef_v (dt_mes_competencia, dt_mes_competencia_param, cd_estabelecimento, qt_beneficiarios, qt_consultas, qt_exames) AS select	substr(dt_mes_competencia,1,20) dt_mes_competencia,
	dt_mes_competencia dt_mes_competencia_param,
	cd_estabelecimento,
	sum(qt_beneficiarios) qt_beneficiarios,
	sum(qt_consultas) qt_consultas,
	sum(qt_exames) qt_exames
FROM (
	select	dt_mes_competencia_param dt_mes_competencia,
		cd_estabelecimento,
		count(*) qt_beneficiarios,
		0 qt_consultas,
		0 qt_exames
	from	eis_ops_beneficiarios_v
	group by dt_mes_competencia_param, cd_estabelecimento
	
union all

	select	dt_mes_competencia,
		cd_estabelecimento,
		0 qt_beneficiarios,
		qt_consultas,
		qt_exames
	from	eis_ops_exames_consulta_v
	) alias5
group by dt_mes_competencia, cd_estabelecimento;

