CREATE OR REPLACE VIEW eis_ops_exames_prestador_v (ds_tipo_despesa, nr_seq_prest_solic, nm_prestador_solic, nr_seq_prestador_exec, nm_prestador_exec, dt_mes_competencia, dt_mes_competencia_param, cd_estabelecimento, vl_exame, qt_exames) AS select	substr(h.ds_grupo,1,255) ds_tipo_despesa,
	v.nr_seq_prest_solic,
	substr(pls_obter_dados_prestador(v.nr_seq_prest_solic,'N'),1,255) nm_prestador_solic,
	v.nr_seq_prestador_exec,
	substr(pls_obter_dados_prestador(v.nr_seq_prestador_exec,'N'),1,255) nm_prestador_exec,
	v.dt_mes_competencia,
	v.dt_mes_competencia dt_mes_competencia_param,
	v.cd_estabelecimento,
	v.vl_item vl_exame,
	count(*) qt_exames
FROM 	pls_conta_proc cp,
	eis_ops_custo_v v,
	ans_grupo_despesa h
where 	v.nr_seq_conta_proc = cp.nr_sequencia
and 	v.nr_seq_conta = cp.nr_seq_conta
and 	cp.nr_seq_grupo_ans = h.nr_sequencia
and 	h.ie_tipo_grupo_ans = '10'
group by h.ds_grupo,
	 v.nr_seq_prest_solic,
