CREATE OR REPLACE VIEW eis_projecao_receita_v (dt_atualizacao, nm_usuario, cd_estabelecimento, cd_conta_contabil, ds_conta_contabil, cd_centro_custo, ds_centro_custo, dt_referencia, vl_projecao, vl_faturado, vl_faturado_ant, vl_orcado, vl_diferenca, pr_diferenca, qt_dias) AS select	a.dt_atualizacao,
	a.nm_usuario,
	a.cd_estabelecimento,
	a.cd_conta_contabil,
	substr(obter_desc_conta_contabil(a.cd_conta_contabil),1,255) ds_conta_contabil,
	a.cd_centro_custo,
	substr(obter_desc_centro_custo(a.cd_centro_custo),1,80) ds_centro_custo,
	a.dt_referencia,
	a.vl_projecao,
	a.vl_faturado,
	a.vl_faturado_ant,
	a.vl_orcado,
	(a.vl_projecao - a.vl_orcado) vl_diferenca,
	(dividir((a.vl_projecao - a.vl_orcado), a.vl_projecao) * 100) pr_diferenca,
	a.qt_dias
FROM	eis_projecao_receita a;

