CREATE OR REPLACE VIEW eis_pront_same_hist_v (qt_total, dt_referencia, nr_seq_operacao, ds_operacao) AS select	1 qt_total,
	a.dt_atualizacao_nrec dt_referencia,
	b.nr_seq_operacao,
	substr(obter_desc_same_operacao(b.nr_seq_operacao),1,40) ds_operacao
FROM	same_prontuario a,
	same_prontuario_hist b
where	a.nr_sequencia	 = b.nr_seq_same;

