CREATE OR REPLACE VIEW eis_quimio_v (ds_setor, nm_paciente, nm_manipulador, qt_manipulador, nr_sequencia, cd_setor_paciente, cd_pessoa_fisica, cd_manipulador, dt_referencia, cd_material, ds_material, cd_grupo_material, ds_grupo_material, cd_subgrupo_material, ds_subgrupo_material, cd_classe_material, ds_classe_material, cd_estabelecimento, ds_estabelecimento, cd_especialidade, ds_especialidade, cd_protocolo, nm_protocolo, ds_custo, ds_medicacao, nr_seq_medicacao, ie_primeiro_dia_ciclo, nm_profissional, nr_seq_atendimento) AS select 	substr(obter_nome_setor(a.cd_setor_paciente),1,200) ds_setor,
	substr(obter_nome_pf(a.cd_pessoa_fisica),1,200) nm_paciente, 
	substr(obter_nome_pf(a.cd_manipulador),1,200) nm_manipulador, 
	count(a.cd_manipulador) qt_manipulador, 
	a.nr_sequencia, 
	a.cd_setor_paciente, 
