	substr(OBTER_NOME_ESTABELECIMENTO(h.CD_ESTABELECIMENTO),1,100) DS_ESTABELECIMENTO, 
    substr(OBTER_ESPECIALIDADE_MEDICO(h.CD_MEDICO_RESP,'C'),1,5) CD_ESPECIALIDADE, 
	substr(OBTER_NOME_ESPECIALIDADE(substr(OBTER_ESPECIALIDADE_MEDICO(h.CD_MEDICO_RESP,'C'),1,5)),1,100) DS_ESPECIALIDADE,   
	h.cd_protocolo, 
	substr(obter_desc_protocolo(h.cd_protocolo),1,255) NM_PROTOCOLO, 
	substr(CASE WHEN obter_dados_material_estab(b.cd_material, wheb_usuario_pck.get_cd_estabelecimento, 'CU')='B' THEN 'Baixo' WHEN obter_dados_material_estab(b.cd_material, wheb_usuario_pck.get_cd_estabelecimento, 'CU')='M' THEN  'Médio' WHEN obter_dados_material_estab(b.cd_material, wheb_usuario_pck.get_cd_estabelecimento, 'CU')='A' THEN  'Alto' END ,1,30) ds_custo, 
	substr(obter_desc_protocolo_medic(h.NR_SEQ_MEDICACAO,h.CD_PROTOCOLO),1,50) ds_medicacao, 
	h.NR_SEQ_MEDICACAO, 
	qt_obter_se_prim_ciclo_dia(g.nr_seq_atendimento) ie_primeiro_dia_ciclo, 
	substr(obter_nome_usuario(g.NM_USUARIO_INICIO_ADM),1,200) nm_profissional, 
	g.nr_seq_atendimento 
from 	paciente_setor h, 
	paciente_atendimento g, 
	estrutura_material_v c, 
	prescr_material b 
where		g.NR_PRESCRICAO = b.nr_prescricao 
and 	c.cd_material = b.cd_material 
and		g.nr_seq_paciente = h.nr_seq_paciente 
and  not exists (select 1 from can_ordem_prod x where x.nr_seq_atendimento = g.nr_seq_atendimento)  
group by obter_nome_setor(h.cd_setor_atendimento), 
