	h.cd_setor_atendimento, 
	h.cd_pessoa_fisica, 
	h.dt_protocolo, 
	b.cd_material, 
	obter_desc_material(b.cd_material), 
	CD_GRUPO_MATERIAL, 
	DS_GRUPO_MATERIAL, 
	CD_SUBGRUPO_MATERIAL, 
	DS_SUBGRUPO_MATERIAL, 
	CD_CLASSE_MATERIAL, 
	DS_CLASSE_MATERIAL, 
	h.CD_ESTABELECIMENTO, 
	substr(OBTER_NOME_ESTABELECIMENTO(h.CD_ESTABELECIMENTO),1,100), 
	substr(OBTER_ESPECIALIDADE_MEDICO(h.CD_MEDICO_RESP,'C'),1,5), 
	substr(OBTER_NOME_ESPECIALIDADE(substr(OBTER_ESPECIALIDADE_MEDICO(h.CD_MEDICO_RESP,'C'),1,5)),1,100), 
