CREATE OR REPLACE VIEW eis_saldo_financeiro_v (vl_saldo_cb, vl_saldo_te, vl_saldo_total, dt_referencia, cd_estabelecimento, cd_empresa) AS select	obter_saldo_caixa_banco(a.dt_dia,c.cd_estabelecimento,'CB') vl_saldo_cb,
	obter_saldo_caixa_banco(a.dt_dia,c.cd_estabelecimento,'TE') vl_saldo_te,
