CREATE OR REPLACE VIEW escala_documentacao_v (ie_escala, ds_escala, nr_sequencia, ie_restricao_uso) AS SELECT  DISTINCT(ie_escala),
        ds_escala,
        nr_Sequencia,
        ie_restricao_uso
FROM (
            SELECT  ie_escala,
                    obter_valor_dominio(1799,ie_escala) ds_escala,
                    nr_sequencia,
                    ie_restricao_uso
            FROM    escala_documentacao
            WHERE   Obter_se_Valor_Dominio_ativo(1799,ie_escala) = 'S'

            
UNION ALL

            
            SELECT  ie_escala,
                    ds_caption ds_escala,
                    nr_sequencia,
                    ie_restricao_uso
            FROM	escala_documentacao
            WHERE	Obter_se_Valor_Dominio_ativo(1799,ie_escala) = 'S'
        ) alias4
ORDER BY    ds_escala;

