CREATE OR REPLACE VIEW estrutura_produto_v (cd_estrutura, ds_estrutura, ie_nivel_estrutura, cd_estrutura_superior, ds_objetivo_estrutura, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, nivel) AS WITH RECURSIVE cte AS (
SELECT A.CD_ESTRUTURA,A.DS_ESTRUTURA,A.IE_NIVEL_ESTRUTURA,A.CD_ESTRUTURA_SUPERIOR,A.DS_OBJETIVO_ESTRUTURA,A.DT_ATUALIZACAO,A.NM_USUARIO,A.DT_ATUALIZACAO_NREC,A.NM_USUARIO_NREC,1 NIVEL
 FROM Estrutura_Produto A WHERE A.CD_ESTRUTURA_SUPERIOR IS NULL
  UNION ALL
SELECT A.CD_ESTRUTURA,A.DS_ESTRUTURA,A.IE_NIVEL_ESTRUTURA,A.CD_ESTRUTURA_SUPERIOR,A.DS_OBJETIVO_ESTRUTURA,A.DT_ATUALIZACAO,A.NM_USUARIO,A.DT_ATUALIZACAO_NREC,A.NM_USUARIO_NREC,(c.level+1) NIVEL
 FROM Estrutura_Produto A JOIN cte c ON (c.CD_ESTRUTURA = A.CD_ESTRUTURA_SUPERIOR)

) SELECT * FROM cte;


