SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW execucao_mpo_v (nr_consistencia, nr_sequencia, ds_consistencia) AS select	1 nr_consistencia,
	a.nr_sequencia,
	substr(obter_valor_dominio(4958,1),1,255)ds_consistencia
FROM	proj_projeto a
where	exists (    select 	1
		    from	proj_cronograma x,
		    		proj_cron_etapa y
		    where	y.nr_seq_cronograma = x.nr_sequencia
		    and		a.nr_sequencia = x.nr_seq_proj
		    and		y.ie_mpo = 'S'
		    and		y.ie_aderencia	 = 'S'
		    and		y.pr_etapa = 100)
and	not exists (select 	1
		    from	proj_documento x
		    where	x.nr_seq_proj = a.nr_sequencia
		    and		x.nr_seq_tipo_documento = 4);

