CREATE OR REPLACE VIEW far_busca_preco_hjrp_v (cd_barra_material, ds_material, vl_preco) AS select	c.cd_barra_material,
	a.ds_material,
	far_obter_preco_material(a.cd_material,b.cd_estabelecimento,null) vl_preco
FROM	material a,
	material_estab b,
	material_cod_barra c
where	a.cd_material = b.cd_material
and	c.cd_material = a.cd_material
and	b.ie_farmacia_com = 'S'
and	b.cd_estabelecimento = 1
and 	far_obter_preco_material(a.cd_material,b.cd_estabelecimento,null) is not null;

