SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW forma_pago_sat_v (metodopago, cd, ds, nr_seq_apresent) AS select 	'PUE' metodoPago,
			a.vl_dominio cd,
			obter_desc_expressao(a.cd_exp_valor_dominio, a.ds_valor_dominio) ds,
			nr_seq_apresent
	FROM 	valor_dominio a
	where 	a.cd_dominio = '10825'
	and		a.vl_dominio = '1'
	
union

	select 	'PPD' metodoPago,
			a.vl_dominio cd,
			obter_desc_expressao(a.cd_exp_valor_dominio, a.ds_valor_dominio) ds,
			nr_seq_apresent
	from 	valor_dominio a
	where 	a.cd_dominio = '10825'
	and		a.vl_dominio != '1'
	
union

	select 	'NONE' metodoPago,
			a.vl_dominio cd,
			obter_desc_expressao(a.cd_exp_valor_dominio, a.ds_valor_dominio) ds,
			nr_seq_apresent
	from 	valor_dominio a
	where 	a.cd_dominio = '10825'
	order by nr_seq_apresent;

