SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW grafico_disp_item_html_v (x, y, y_min, y_max, nr_sequencia, code_group) AS select  a.dt_instalacao x,
        1 y,
	null y_min,
	null y_max,
        a.nr_sequencia nr_sequencia,
	'D' code_group
FROM    atend_pac_dispositivo a

union  all

select coalesce(a.dt_retirada, LOCALTIMESTAMP) x,
       1 y,
       null y_min,
       null y_max,
       a.nr_sequencia nr_sequencia,
	'D' code_group
from   atend_pac_dispositivo a

union all

/*Equipamentos*/

select  a.dt_inicio x,
        1 y,
	null y_min,
	null y_max,
        a.nr_sequencia nr_sequencia,
	'E' code_group
from    equipamento_cirurgia a

union  all

select coalesce(a.dt_fim, LOCALTIMESTAMP) x,
       1 y,
       null y_min,
       null y_max,
       a.nr_sequencia nr_sequencia,
	'E' code_group
from   equipamento_cirurgia a;

