SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW hcbcs_painel_exames_rel_v (ds_tipo_exame, cd_estabelecimento, qt_int, qt_ext, qt_ps, qt_outros_mun, qt_sus, qt_part, qt_conv) AS select	/*+ INDEX (A PROPACI_I4) INDEX (Q CONPACI_PK) */	SUBSTR(coalesce(obter_valor_dominio(95, p.cd_tipo_procedimento),'Não definido'),1,100) ds_tipo_exame, 
	q.cd_estabelecimento, 
	sum(CASE WHEN b.ie_tipo_atendimento=1 THEN  a.qt_procedimento  ELSE 0 END ) qt_int, 
	sum(CASE WHEN b.ie_tipo_atendimento=7 THEN  a.qt_procedimento  ELSE 0 END ) qt_ext, 
	sum(CASE WHEN b.ie_tipo_atendimento=3 THEN  a.qt_procedimento WHEN b.ie_tipo_atendimento=8 THEN  a.qt_procedimento  ELSE 0 END ) qt_ps, 
	sum(CASE WHEN b.ie_tipo_atendimento=7 THEN  CASE WHEN obter_compl_pf(b.cd_pessoa_fisica,1,'CDM')=430300 THEN  0  ELSE a.qt_procedimento END   ELSE 0 END ) qt_outros_mun, 
	sum(CASE WHEN b.ie_tipo_atendimento=7 THEN  CASE WHEN obter_tipo_convenio(q.cd_convenio_parametro)=3 THEN a.qt_procedimento  ELSE 0 END   ELSE 0 END ) qt_sus, 
	sum(CASE WHEN b.ie_tipo_atendimento=7 THEN  CASE WHEN obter_tipo_convenio(q.cd_convenio_parametro)=1 THEN a.qt_procedimento  ELSE 0 END   ELSE 0 END ) qt_part, 
	sum(CASE WHEN b.ie_tipo_atendimento=7 THEN  CASE WHEN obter_tipo_convenio(q.cd_convenio_parametro)=2 THEN a.qt_procedimento  ELSE 0 END   ELSE 0 END ) qt_conv 
FROM 	procedimento p, 
	conta_paciente q, 
	atendimento_paciente b, 
	procedimento_paciente a 
where	a.cd_procedimento = p.cd_procedimento 
and	a.ie_origem_proced = p.ie_origem_proced 
and	a.nr_interno_conta = q.nr_interno_conta 
and 	b.nr_atendimento = q.nr_atendimento 
and 	a.dt_procedimento between TRUNC(LOCALTIMESTAMP,'month') and fim_dia(last_Day(LOCALTIMESTAMP)) 
and 	a.cd_motivo_exc_conta is null  
and	a.nr_sequencia <> coalesce(a.nr_seq_proc_pacote,0)  
and 	p.cd_tipo_procedimento in (2,3,4,5,33,34) 
group by SUBSTR(coalesce(obter_valor_dominio(95, p.cd_tipo_procedimento),'Não definido'),1,100), 
	q.cd_estabelecimento;

