CREATE OR REPLACE VIEW hdjb_ipe_exames_v (tp_registro, nr_seq_protocolo, nm_sistema, cd_cgc_hospital, qt_total_conta, qt_total_lancamento, cd_interno, nm_hospital, ds_espaco, ie_zeros, tp_nota, nr_folha, qt_lancamento, qt_matricula, cd_usuario_convenio, cd_prestador, tp_prestador, dt_referencia, nr_interno_conta, vl_total_conta, nr_linha, dt_item, cd_item_convenio, qt_item, vl_matmed, vl_total_matmed, nm_paciente, cd_solicitante, nm_arquivo_pdf) AS SELECT
	1				tp_registro,
	a.nr_seq_protocolo		nr_seq_protocolo,
	'SMH'				nm_sistema,
	a.cd_cgc_hospital		cd_cgc_hospital,
	MAX(c.nr_folha)			qt_total_conta,
	COUNT(*)			qt_total_lancamento,
	'03004515'			cd_interno,
	SUBSTR(a.nm_hospital,1,45)	nm_hospital,
	' '				ds_espaco,
	0				ie_zeros,
	0				tp_nota,
	0				nr_folha,
	0				qt_lancamento,
	0				qt_matricula,
	' '				cd_usuario_convenio,
	0				cd_prestador,
	3				tp_prestador,
