	0				nr_linha,
	LOCALTIMESTAMP				dt_item,
	0				cd_item_convenio,
	0				qt_item,
	0				vl_matmed,
	0				vl_total_matmed,
	' '				nm_paciente,
	0 				cd_solicitante,
	' '				nm_arquivo_pdf
FROM	w_interf_conta_item_ipe c,
	w_interf_conta_header a
WHERE	a.nr_seq_protocolo		= c.nr_seq_protocolo
GROUP BY
	a.nr_seq_protocolo,
