	0				qt_total_conta,
	0				qt_total_lancamento,
	'0'				cd_interno,
	' '				nm_hospital,
	' '				ds_espaco,
	0 				ie_zeros,
	35				tp_nota,
	0				nr_folha,
	COUNT(*)			qt_lancamento,
	0				qt_matricula,
	' '				cd_usuario_convenio,
	somente_numero(c.cd_cgc_hospital)
