	3				tp_prestador,
	MIN(c.dt_item)			dt_referencia,
	c.nr_seq_conta_convenio	nr_interno_conta,
	trunc(sum(CASE WHEN c.cd_item_convenio=32200005 THEN (round(c.vl_total_item/0.0645)*0.0645)  ELSE c.vl_total_item END ),2) vl_total_conta,
	0				nr_linha,
	LOCALTIMESTAMP				dt_item,
	0				cd_item_convenio,
	0				qt_item,
	0				vl_matmed,
	trunc(sum(CASE WHEN c.cd_item_convenio=32200005 THEN (round(c.vl_total_item/0.0645)*0.0645)  ELSE 0 END ),2) vl_total_matmed,
	' '				nm_paciente,
