	c.nr_linha			nr_linha,
	c.dt_item			dt_item,
	c.cd_item_convenio		cd_item_convenio,
	CASE WHEN c.cd_item_convenio='32200005' THEN round(c.vl_total_item/0.0645)  ELSE c.qt_item END  qt_item,
