SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW hepic_detalhe_cirurgia_v (nr_cirurgia, dt_registro, dt_inicio_real, dt_fim_cirurgia, cd_procedimento, ie_origem_proced) AS select distinct
		a.nr_cirurgia, 
		b.dt_inicio_prevista dt_registro, 
		b.dt_inicio_real, 
		b.dt_fim_cirurgia, 
		a.cd_procedimento, 
		a.ie_origem_proced 
 FROM 	procedimento_paciente a, 
		cirurgia b, 
		hepic_cirurgia_v c 
 where 	a.nr_cirurgia is not null 
  and 	a.nr_cirurgia = b.nr_cirurgia  
  and 	Obter_Se_Proc_Cirurgico(a.cd_procedimento, a.ie_origem_proced,a.cd_setor_atendimento) = 'S' 
  and a.nr_cirurgia = c.nr_cirurgia 
order by a.nr_cirurgia;

