SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW hepic_lab_medicamento_v (cd_medicamento, ds_medicamento, ie_situacao) AS select  m.cd_medicamento,
			m.ds_medicamento,
			m.ie_situacao
	FROM    cih_medicamento m,
			cih_medicamento_int i,
			equipamento_lab e
	where   i.cd_medicamento  = m.cd_medicamento
	and     i.cd_equipamento  = e.cd_equipamento
	and     e.ds_sigla        = 'HEPIC'
	order by m.cd_medicamento;

