SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW hepic_lab_metodo_v (nr_sequencia, ds_metodo, ie_situacao) AS select  m.nr_sequencia,
			m.ds_metodo,
			m.ie_situacao
	FROM    metodo_exame_lab m,
			metodo_exame_lab_int i,
			equipamento_lab e
	where   i.nr_seq_metodo   = m.nr_sequencia
	and     i.cd_equipamento  = e.cd_equipamento
	and     e.ds_sigla        = 'HEPIC'
	order by m.ds_metodo;

