SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW hepic_lab_microrganismo_v (cd_microorganismo, ds_microorganismo, ie_situacao) AS select  m.cd_microorganismo,
			m.ds_microorganismo,
			m.ie_situacao
	FROM    cih_microorganismo m,
			cih_microorganismo_int i,
			equipamento_lab e
	where   i.cd_microorganismo = m.cd_microorganismo
	and     i.cd_equipamento    = e.cd_equipamento
	and     e.ds_sigla          = 'HEPIC'
	order by m.cd_microorganismo;

