CREATE OR REPLACE VIEW hepic_salas_cirurgicas_v (cd_unidade_basica, ds_unidade_atend, cd_setor_atendimento, ds_setor_atendimento) AS SELECT 	a.cd_unidade_basica,
			substr((a.cd_unidade_compl || a.cd_unidade_basica),1,255) ds_unidade_atend,
			a.cd_setor_atendimento,
			b.ds_setor_atendimento
     FROM 	unidade_atendimento a,
			setor_atendimento b
    WHERE 	a.cd_setor_atendimento = b.cd_setor_atendimento
      AND 	a.ie_situacao = 'A'
      AND 	b.ie_situacao = 'A';

