	' '						sg_estado, 
	' '						ie_emissao_lote, 
	0						ie_tipo_movimento, 
	0						cd_movimento, 
	substr(d.nr_bloqueto,1,3)			cd_banco, 
	c.cd_camara_compensacao				cd_compensacao, 
	d.nm_favorecido					nm_favorecido, 
	d.nr_titulo					nr_documento, 
	d.dt_emissao					dt_lancamento, 
	coalesce(d.vl_saldo_titulo,0)			vl_saldo_titulo, 
	'S'						ie_emissao_individual, 
	' '						nm_pagador, 
	' '						ds_informacoes, 
	' '						ds_aviso, 
	0						qt_registros, 
	0						vl_total_pagto, 
	substr(d.nr_bloqueto,4,1)			cd_moeda, 
	substr(d.nr_bloqueto,5,1)			nr_digito_barras, 
	LOCALTIMESTAMP						dt_vencimento, 
	0						vl_titulo, 
	0						vl_desconto, 
	0						vl_acrescimo, 
	LOCALTIMESTAMP						dt_pagamento, 
	0						vl_pagto, 
	0						qt_moeda, 
	' '						cd_sacado,	 
	0						qt_lotes_arquivos, 
	0						qt_total_registros, 
	c.ie_tipo_pagamento				tp_pagamento, 
	substr(d.nr_bloqueto,6,4)				nr_fator_vencimento, 
	coalesce(d.vl_saldo_multa,0) + coalesce(d.vl_saldo_juros,0) vl_mora, 
	b.cd_carteira					cd_carteira, 
	' '						ds_brancos, 
	'3' 						ie_registro, 
	substr(d.nr_bloqueto,10,10)			vl_barras, 
	substr(d.nr_bloqueto,20,25)			ds_campo_livre, 
	'0'						nr_titulos, 
	6						ie_ordem 
from	estabelecimento e, 
	banco_estabelecimento_v b, 
	banco_escritural a, 
	titulo_pagar_v2 d, 
	titulo_pagar_escrit c 
where	c.nr_titulo		= d.nr_titulo 
and	a.nr_sequencia		= c.nr_seq_escrit 
and	a.cd_estabelecimento	= e.cd_estabelecimento 
and 	b.ie_tipo_relacao	in ('EP','ECC') 
and	b.nr_sequencia		= a.nr_seq_conta_banco 
and 	c.ie_tipo_pagamento	= 'BLQ' 

union
 
/*	trailler do lote - bloquetos	*/
 
select	9						tp_registro, 
	' '						nr_inscricao, 
	somente_numero(coalesce(b.nr_contrato,'666220'))	nr_contrato, 
	e.nr_sequencia					nr_seq_lote, 
	0						cd_agencia, 
	0						cd_conta, 
	' '						nr_digito_conta, 
	' '						nm_empresa, 
	' '						nm_banco, 
	LOCALTIMESTAMP						dt_arquivo, 
	LOCALTIMESTAMP 					hr_arquivo, 
	0						ie_tipo_servico, 
	CASE WHEN c.ie_tipo_pagamento='CC' THEN '01' WHEN c.ie_tipo_pagamento='CCP' THEN '05' WHEN c.ie_tipo_pagamento='BLQ' THEN CASE WHEN c.cd_banco=399 THEN '30'  ELSE '31' END  WHEN c.ie_tipo_pagamento='DOC' THEN '03' END  ie_forma_lancamento, 
	' '						ds_mensagem, 
	' '						ds_endereco, 
	' '						nr_endereco, 
	' '						ds_complemento, 
	' '						ds_bairro, 
	' '						ds_cidade, 
	' '						cd_cep, 
	' '						sg_estado, 
	' '						ie_emissao_lote, 
	0						ie_tipo_movimento, 
	00						cd_movimento, 
	'0'						cd_banco, 
	0						cd_compensacao, 
	' '						nm_favorecido, 
	0						nr_documento, 
	LOCALTIMESTAMP						dt_lancamento, 
	0						vl_saldo_titulo, 
	'S'						ie_emissao_individual, 
	' '						nm_pagador, 
	' '						ds_informacoes, 
	' '						ds_aviso, 
	count(*)					qt_registros, 
	sum(coalesce(c.vl_escritural,0) - coalesce(c.vl_desconto,0) + coalesce(c.vl_acrescimo,0)) vl_total_pagto, 
	'0'						cd_moeda, 
	' '						nr_digito_barras, 
	LOCALTIMESTAMP						dt_vencimento, 
	0						vl_titulo, 
	0						vl_desconto, 
	0						vl_acrescimo, 
	LOCALTIMESTAMP						dt_pagamento, 
	0						vl_pagto, 
	0						qt_moeda, 
	' '						cd_sacado, 
	0						qt_lotes_arquivos, 
	0						qt_total_registros, 
	c.ie_tipo_pagamento				tp_pagamento, 
	' '						nr_fator_vencimento, 
	0						vl_mora, 
	' '						cd_carteira, 
	' '						ds_brancos, 
	'5' 						ie_registro, 
	'0'						vl_barras, 
	'0'						ds_campo_livre, 
	'0'						nr_titulos, 
	7						ie_ordem 
from	banco_estabelecimento_v b, 
	estabelecimento a,    
	banco_escritural e, 
	titulo_pagar_escrit c 
where	e.nr_sequencia		= c.nr_seq_escrit 
and	e.cd_estabelecimento  = a.cd_estabelecimento 
and	b.ie_tipo_relacao   	in ('EP','ECC') 
and	b.nr_sequencia		= e.nr_seq_conta_banco 
and 	c.ie_tipo_pagamento	= 'BLQ' 
group by e.nr_sequencia, 
	c.ie_tipo_pagamento, 
	CASE WHEN c.ie_tipo_pagamento='CC' THEN '01' WHEN c.ie_tipo_pagamento='CCP' THEN '05' WHEN c.ie_tipo_pagamento='BLQ' THEN CASE WHEN c.cd_banco=399 THEN '30'  ELSE '31' END  WHEN c.ie_tipo_pagamento='DOC' THEN '03' END , 
	b.nr_contrato 

union
 
/*	trailler do arquivo	*/
 
select	10						tp_registro, 
	' '						nr_inscricao, 
	somente_numero(coalesce(b.nr_contrato,'666220'))	nr_contrato, 
	e.nr_sequencia					nr_seq_lote, 
	0						cd_agencia, 
	0						cd_conta, 
	' '						nr_digito_conta, 
	' '						nm_empresa, 
	' '						nm_banco, 
	LOCALTIMESTAMP						dt_arquivo, 
	LOCALTIMESTAMP 					hr_arquivo, 
	0						ie_tipo_servico, 
	'0'						ie_forma_lancamento, 
	' '						ds_mensagem, 
	' '						ds_endereco, 
	' '						nr_endereco, 
	' '						ds_complemento, 
	' '						ds_bairro, 
	' '						ds_cidade, 
	' '						cd_cep, 
	' '						sg_estado, 
	' '						ie_emissao_lote, 
	0						ie_tipo_movimento, 
	0						cd_movimento, 
	'0'						cd_banco, 
	0						cd_compensacao, 
	' '						nm_favorecido, 
	0						nr_documento, 
	LOCALTIMESTAMP						dt_lancamento, 
	0						vl_saldo_titulo, 
	' '						ie_emissao_individual, 
	' '						nm_pagador, 
	' '						ds_informacoes, 
	' '						ds_aviso, 
	0						qt_registros, 
	0						vl_total_pagto, 
	'0'						cd_moeda, 
	' '						nr_digito_barras, 
	LOCALTIMESTAMP						dt_vencimento, 
	0						vl_titulo, 
	0						vl_desconto, 
	0						vl_acrescimo, 
	LOCALTIMESTAMP						dt_pagamento, 
	0						vl_pagto, 
	0						qt_moeda, 
	' '						cd_sacado, 
	count(*)					qt_lotes_arquivos, 
	count(*)					qt_total_registros, 
	' '						tp_pagamento, 
	' '						nr_fator_vencimento, 
	0						vl_mora, 
	' '						cd_carteira, 
	' '						ds_brancos, 
	'9' 						ie_registro, 
	'0'						vl_barras, 
	'0'						ds_campo_livre, 
	'0'						nr_titulos, 
	8						ie_ordem 
from	banco_estabelecimento_v b, 
	estabelecimento a,    
	banco_escritural e 
where	e.cd_estabelecimento  = a.cd_estabelecimento 
and	b.ie_tipo_relacao    in ('EP','ECC') 
and	b.nr_sequencia		= e.nr_seq_conta_banco 
group by	e.nr_sequencia, 
	b.nr_contrato 
order by	ie_ordem, 
	nr_documento;

