CREATE OR REPLACE VIEW hscf_mapa_dieta_v (dt_dieta, ie_dieta_lib, cd_unidade, cd_refeicao, ie_destino_dieta, cd_setor_atendimento, cd_dieta, nm_dieta, cd_pessoa_fisica) AS select	distinct
	a.dt_dieta, 
	(select max(x.ie_lib_dieta) FROM atend_categoria_convenio x where x.nr_atendimento = a.nr_atendimento and x.dt_inicio_vigencia = (select max(z.dt_inicio_vigencia) from atend_categoria_convenio z where z.nr_atendimento = x.nr_atendimento)) ie_dieta_lib, 
	a.cd_unidade, 
	a.cd_refeicao, 
	a.ie_destino_dieta, 
	c.cd_setor_atendimento, 
	a.cd_dieta, 
	a.nm_dieta, 
	a.cd_pessoa_fisica 
from	setor_atendimento c, 
	mapa_dieta_v a 
where	c.cd_setor_atendimento = a.cd_setor_atendimento 
and	a.ie_status = 'A' 	 
order by c.cd_setor_atendimento, a.cd_unidade;

