CREATE OR REPLACE VIEW ind_gestao_recurso_glosa_v (ds_item, ds_motivo_glosa, ds_acao_glosa, dt_atualizacao, vl_glosa_total, vl_glosa_aceita, vl_reapresentacao) AS select  substr(obter_dados_lote_audit_item(nr_sequencia,'IT'),1,255) ds_item,
