SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW integr_gerdau_observ_nf_ent_v (tp_regsitro, dt_emissao, cd_estabelecimento, vl_registro) AS select	4	tp_regsitro,
	n.dt_emissao,
	n.cd_estabelecimento,
	'GG    '				||
	'FOB   ' 				||
	'FOB   '				||
	'L' || rpad(n.nr_sequencia, 7, ' ')	||
	'999'				||
	'1'				||
	rpad(' ', 60, ' ')			||
	'*'				vl_registro
FROM	nota_fiscal n
where	obter_se_nota_entrada_saida(n.nr_sequencia) = 'E';

