SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW inv_v2 (ds_comando, qt_dep) AS SELECT	SUBSTR('ALTER '|| A.OBJECT_TYPE||' '||OBJECT_NAME||' COMPILE;',1,70) ds_comando,
	COUNT(*) QT_DEP
FROM user_objects a
LEFT OUTER JOIN user_dependencies d ON (A.OBJECT_NAME = D.REFERENCED_NAME)
WHERE A.OBJECT_TYPE IN ('PROCEDURE', 'VIEW', 'TRIGGER',
			'PACKAGE', 'PACKAGE BODY','FUNCTION') AND A.STATUS = 'INVALID' GROUP BY SUBSTR('ALTER '|| A.OBJECT_TYPE||' '||OBJECT_NAME||' COMPILE;',1,70)
ORDER BY QT_DEP;

