SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW inv_v3 (ds_comando, ds_erro, nm_objeto) AS select	substr('ALTER ' || o.object_type || ' ' || o.object_name  || ' COMPILE;',1,60) ds_comando,
	substr(e.text,1,60) ds_erro,
	e.name nm_objeto
FROM	user_errors e,
	user_objects o
where	e.name = o.object_name
and	o.object_type in ('PROCEDURE', 'VIEW', 'TRIGGER', 'PACKAGE','PACKAGE BODY', 'FUNCTION')
and	o.status = 'INVALID'
and	substr(e.text,1,9) not in ('PLS-00905','ORA-06575','ORA-04063')
and	substr(e.text,1,17) not in ('PL/SQL: ORA-06575', 'PL/SQL: ORA-04063')
and	e.sequence = 1
order by 1;

