SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW itens_nao_administrados_v (ie_tipo, nr_atendimento, nr_prescricao, cd_mat_proc, ds_mat_proc, ds_horarios, dt_horario) AS select  1 ie_tipo,
		a.nr_atendimento nr_atendimento,
		a.nr_prescricao nr_prescricao,
		b.cd_material cd_mat_proc,
		obter_desc_material(b.cd_Material) ds_mat_proc,
		b.ds_horarios ds_horarios,
		c.dt_horario dt_horario
FROM 	prescr_mat_hor c,
		prescr_material b,
		prescr_medica a
where	a.nr_prescricao	= b.nr_prescricao
and		c.NR_SEQ_MATERIAL	= b.nr_sequencia
and		c.nr_prescricao		= a.nr_prescricao
and		a.dt_prescricao		> LOCALTIMESTAMP - interval '2 days'
and		adep_obter_regra_inclusao(CASE WHEN b.ie_agrupador=1 THEN 'MED' WHEN b.ie_agrupador=2 THEN 'MAT' WHEN b.ie_agrupador=8 THEN 'SNE' WHEN b.ie_agrupador=12 THEN 'SO' WHEN b.ie_agrupador=4 THEN 'SOL' END , wheb_usuario_pck.get_cd_estabelecimento, 0,
		obter_perfil_ativo, b.cd_material, null, null, null,a.cd_setor_Atendimento,null, a.nr_prescricao,null) = 'S'
and		coalesce(a.dt_liberacao, a.dt_liberacao_medico) is not null
and		c.dt_suspensao 		is null
and		b.ie_suspenso		= 'N'
and		c.dt_fim_horario	is null
and		Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
and		coalesce(c.ie_situacao,'A') = 'A'
and		coalesce(c.ie_adep,'S') = 'S'
and		b.ie_Agrupador in (1,4,12)
and		coalesce(c.ie_horario_especial,'N') <> 'S'
and		coalesce(a.ie_adep,'S') = 'S'

union

select	2,
		a.nr_atendimento,
		a.nr_prescricao,
		x.cd_procedimento,
		substr(Obter_Desc_Prescr_Proc(x.cd_procedimento, x.ie_origem_proced, x.nr_seq_proc_interno),1,240),
		x.ds_horarios,
		c.dt_horario
from	procedimento y,
		prescr_procedimento x,
		prescr_proc_hor c,
		prescr_medica a
where	y.cd_procedimento = x.cd_procedimento
and		y.ie_origem_proced = x.ie_origem_proced
and		y.cd_procedimento = c.cd_procedimento
and		y.ie_origem_proced = c.ie_origem_proced
and		x.nr_prescricao = c.nr_prescricao
and		x.nr_sequencia = c.nr_seq_procedimento
and		x.nr_prescricao = a.nr_prescricao
and		c.nr_prescricao = a.nr_prescricao
and		a.dt_prescricao	> LOCALTIMESTAMP - interval '2 days'
and		x.nr_seq_proc_interno is null
and		x.nr_seq_exame is null
and		x.nr_seq_solic_sangue is null
and		x.nr_seq_derivado is null
and		x.nr_seq_exame_sangue is null
and		x.dt_suspensao is null
and		c.dt_fim_horario is null
and		c.dt_suspensao is null
and		coalesce(c.ie_situacao,'A') = 'A'
and		Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
and		adep_obter_regra_inclusao('PROC', wheb_usuario_pck.get_cd_estabelecimento, 0, obter_Perfil_ativo, null, x.cd_procedimento, x.ie_origem_proced, x.nr_seq_proc_interno, a.cd_setor_atendimento, x.cd_setor_atendimento, a.nr_prescricao, null) = 'S'
and		coalesce(c.ie_horario_especial,'N') <> 'S'

union

select	3,
		a.nr_atendimento,
		a.nr_prescricao,
		x.cd_procedimento,
		substr(Obter_Desc_Prescr_Proc(x.cd_procedimento, x.ie_origem_proced, x.nr_seq_proc_interno),1,240),
		x.ds_horarios,
		c.dt_horario
from	procedimento y,
		proc_interno w,
		prescr_procedimento x,
		prescr_proc_hor c,
		prescr_medica a
where	y.cd_procedimento = x.cd_procedimento
and		y.ie_origem_proced = x.ie_origem_proced
and		y.cd_procedimento = c.cd_procedimento
and		y.ie_origem_proced = c.ie_origem_proced
and		w.nr_sequencia = x.nr_seq_proc_interno
and		w.nr_sequencia = c.nr_seq_proc_interno
and		x.nr_prescricao = c.nr_prescricao
and		x.nr_sequencia = c.nr_seq_procedimento
and		x.nr_prescricao = a.nr_prescricao
and		c.nr_prescricao = a.nr_prescricao
and		a.dt_prescricao	> LOCALTIMESTAMP - interval '2 days'
and		w.ie_tipo <> 'G'
and		w.ie_tipo <> 'BS'
and		coalesce(w.ie_ivc,'N') <> 'S'
and		coalesce(w.ie_ctrl_glic,'NC') = 'NC'
and		x.nr_seq_proc_interno is not null
and		x.nr_seq_prot_glic is null
and		x.nr_seq_exame is null
and		x.nr_seq_solic_sangue is null
and		x.nr_seq_derivado is null
and		x.nr_seq_exame_sangue is null
and		x.dt_suspensao is null
and		c.dt_fim_horario is null
and		c.dt_suspensao is null
and		coalesce(c.ie_situacao,'A') = 'A'
and		Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
and		adep_obter_regra_inclusao('PROC', wheb_usuario_pck.get_cd_estabelecimento, 0, obter_Perfil_ativo, null, x.cd_procedimento, x.ie_origem_proced, x.nr_seq_proc_interno, a.cd_setor_atendimento, x.cd_setor_atendimento, a.nr_prescricao, null) = 'S'
and		coalesce(c.ie_horario_especial,'N') <> 'S';

