SET client_encoding TO 'UTF8';

CREATE OR REPLACE VIEW job_v2 (job, banco, ult_exec, prox_exec, dur_seg, status, falhas, owner, comando, parada, scheduler, job_name) AS SELECT  job ,
                SUBSTR(priv_user,1,10) banco ,
                TO_CHAR(last_date,'dd/mm/yyyy')
                || ' '
                || last_sec ULT_EXEC ,
                TO_CHAR(next_date,'dd/mm/yyyy')
                || ' '
                || next_sec PROX_EXEC ,
                total_time/1000 DUR_SEG ,
                broken STATUS ,
                failures FALHAS ,
                SUBSTR(schema_user,1,10) OWNER ,
                SUBSTR(what,1,255) COMANDO ,
                broken PARADA,
                'N' SCHEDULER,
                '' JOB_NAME
        FROM    user_jobs

UNION ALL

        SELECT  NULL job ,
                job_creator ,
                TO_CHAR(CAST(last_start_date AS timestamp),'dd/mm/yyyy hh24:mi:ss') ,
                TO_CHAR(CAST(next_run_date AS   timestamp),'dd/mm/yyyy hh24:mi:ss') ,
                ABS(extract(DAY FROM (last_run_duration * 86400))) ULT_EXEC ,
                state ,
                failure_count ,
                job_creator ,
                job_action ,
                '' parada,
                'S' "SCHEDULER",
                JOB_NAME
        FROM    user_scheduler_jobs
        ORDER BY 4 DESC;

